import React, { Component } from 'react';
import { Link } from "react-router-dom";
import auth from '../../Helper/auth'



class Login extends Component {

    constructor(props){
        super(props);

        this.state = {
            err:"",
            authenticated:"",
        }

        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit = (event) => {
        event.preventDefault();

        auth.login(this.state.username, this.state.password).then((message) => {
            console.log(message);

            if(message.authenticated){
                this.setState({
                    authenticated: true,
                    err:""
                });
            }
            else{
                this.setState({
                    authenticated: false,
                    err: message.error
                });
            }

        });

    }

    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    render() {
        if(!this.state.authenticated){

            return (
                <div className="login">

                        <div class="vertical-container">

                            <form id='signin-form' onSubmit={this.handleSubmit}>
                                <h2>LOGIN</h2>
                                <input type="text" placeholder="Username" name='username' onChange={this.myChangeHandler}></input>
                                <input type="password" placeholder="Password" name='password' onChange={this.myChangeHandler}></input>
                                <button type="submit" >Sign In</button>
                            </form>

                            <div>
                                {this.state.err}
                            </div>

                        </div>
                </div>
            );
        }

        return (
            <div>Successfully logged in </div>
        )

    }
  }

  export default Login;
