import React, { Component } from 'react';
import Balance from '../../Components/Balance/balance.js'
import Stock from '../../Components/Stock/stock.js'


class Account extends Component {

  constructor(){
    super();

    this.state = {
      stocks :[{name: "APPLE", amount: 5},{name: "BO", amount: 15}, {name: "WCM", amount: 3}, {name: "QYOU", amount: 1}]
    }
  }

  componentDidMount(){
    //obtain account info - name, balance, stocks owned = array of objects

  }

    render() {

      var stocks = [];

      for(var stock in this.state.stocks){
        stocks.push(<Stock value = {stock}/>)
      }


      return (
        <div className="accountPage">

          <Balance balance={2000}/>

          {stocks}

          <button>CANCEL_BUY</button>


          CANCEL_SELL

          SELL

          COMMIT_SELL

          CANCEL_SET_BUY

          CANCEL_SET_SELL

          SET_SELL_AMOUNT

          SET_SELL_TRIGGER

          DISPLAY_SUMMARY

        </div>
      );
    }
  }

  export default Account;
