import React, { Component } from 'react';
import transaction from '../../Helper/transaction';



class Add extends Component {

    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    handleSubmit = (event) => {

        event.preventDefault();

        if(!this.validateForm()){
            return;
        }


        transaction.add(this.state.username, this.state.password).then((message) => {
            console.log(message);

            if(message.authenticated){
                this.setState({
                    authenticated: true,
                    err:""
                });

                this.props.setToken(message.data.token)
            }
            else{
                this.setState({
                    authenticated: false,
                    err: message.error
                });
            }

        });

    }


    render() {

      return (
        <div className="add">

            <form id='signin-form' onSubmit={this.handleSubmit}>
                <input onChange={this.myChangeHandler} type="number"></input>
                <button type="submit" >submit</button>
            </form>

        </div>
      );
    }
  }

  export default Add;
