import axios from 'axios';

class Auth{

    constructor(){


        this.err = "";
        this.authenticated = false;
        this.token = "";
        this.user = "";


        this.login = this.login.bind(this)
        this.setPassword = this.setPassword.bind(this)
        this.logout = this.logout.bind(this)
        this.isAuthenticated = this.isAuthenticated.bind(this)

    }



    setPassword = (password) => {
        console.log("Setpassword --- " + this.password)
        this.password = password
    }

    login = (username, password) => {

        return new Promise(() => {
            axios.post("login", { username: username, password: password }).then( res => {

                if(res.data.error){

                    this.err = res.data.error
                    console.log("The error is " + res.data.error);
                    return false;

                }
                else{

                    this.authenticated = true;
                    this.token = res.data.token;
                    this.user = res.data.user;
                    localStorage.setItem("token", res.data.token);
                    console.log(res);
                    return true;
                }

            }).catch(err => {
                console.log(err)
            })

            console.log("We have run the log in function ")

        })

        //this.setState({username: username, password: password})


    }


    logout = () => {
        console.log("Logging out...")
        this.authenticated = false
    }

    isAuthenticated = () => {
        return this.authenticated
    }
}

export default new Auth();
