import React, { Component } from 'react';
import transaction from '../../Helper/transaction';


class Quote extends Component {

    constructor(){
        super()

        this.state = {
            stock: ""
        }
    }



    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    handleSubmit = (event) => {

        event.preventDefault();

        transaction.quote(this.state.userid, this.state.stock).then((res) => {
            console.log(res.data.data);
            this.setState({message: JSON.stringify(res.data.data)})
        });

    }



    render() {
      return (
        <div className="Quote">

            <form onSubmit={this.handleSubmit}>
                <h1>Search for a stock by symbol</h1>
                <input name="stock" type="text" onChange={this.myChangeHandler}/>
                <button type="submit">Submit</button>
            </form>


            <div>
                {this.state.message}
            </div>

            Buy 

        </div>
      );
    }
  }

  export default Quote;
