import React, { Component } from 'react';
import Order from '../Order/order';


class Stock extends Component {

    constructor(){
        super()

        this.state = {
            sell: false
        }

        console.log("Hello")

    }

    //quote for sell ?
    render() {

        if(!this.state.sell){

            return (
                <div className="stock">


                    <button onClick = {() => this.setState({sell: true})}>
                        Name is: {this.props.name}
                        Amount owned is: {this.props.amount}
                    </button>

                    {this.state.sell && 
                        <div>
                            <Order type="Sell" stockName={this.props.stockName}  stockAmount={this.props.stockAmount}  />
                        </div>
                    }

                </div>
              );

        }


      return (
        <div className="stock">


            <button onClick = {() => this.setState({sell: true})}>
                Name is: {this.props.name}
                Amount owned is: {this.props.amount}
            </button>

            {this.state.sell && 
                <div>
                    <Order type="Sell" stockName={this.props.stockName}  stockAmount={this.props.stockAmount}  />
                </div>
            }

        </div>
      );
    }
  }

  export default Stock;
