import React, { Component } from 'react';
import Buttons from "../Buttons/buttons"


//2 types of orders - triggered and market
class Order extends Component {

    constructor(){
        super()

        this.state = {
            ordertype : "",
            triggeredbuy : false,
            triggeredsell : false
        }

        console.log("Hello")

    }

    handleChange = (event) => {

        if(event.target.value === "triggeredbuy"){
            this.setState({
                triggeredbuy: true
            })
        }
        if(event.target.value === "marketbuy"){
            this.setState({
                triggeredbuy: false
            })
        }
        if(event.target.value === "triggeredsell"){
            this.setState({
                triggeredsell: true
            })
        }
        if(event.target.value === "marketsell"){
            this.setState({
                triggeredsell: false
            })
        }
    }

    handleBuyOrder = () => {

    }

    handleSellOrder = () => {

    }

    render() {

        if(this.props.type === "Buy"){
            return (
                <div className="Order">

                    <h2>Place an order.</h2>

                    <h3>{this.props.type}</h3>

                    <h3>{this.props.stockName}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>

                    <select name="order type" id="ordertype" onChange={this.handleChange}>
                        <option value="marketbuy">Market {this.props.type}</option>
                        <option value="triggered">Set {this.props.type} Trigger</option>
                    </select>


                    <form>
                        {this.props.type} Amount: 
                        <input type="number" min="1"></input>
                        <br/>

                        {this.state.triggered && 
                            <div>
                                {this.props.type} Trigger: 
                                <input type="number" min="1"></input>
                                <br/>

                                <Buttons/>
                                <div>
                                    <button type="submit">Set {this.props.type}</button>
                                    <button type="submit">Commit Set {this.props.type}</button>
                                    <button type="submit">Cancel Set {this.props.type}</button>
                                </div>

                            </div>
                        }

                        {!this.state.triggered && 

                            <div>
                                <button type="submit">{this.props.type}</button>
                                <button type="submit">Commit {this.props.type}</button>
                                <button type="submit">Cancel {this.props.type}</button>
                            </div>

                        }

                    </form>

                </div>
            );
        }


    }
  }

  export default Order;
