import axios from 'axios';


class Transaction{

    constructor(){


        this.stockname = "";
        this.userid = "";
        this.token = "";
        this.amount = "";


        this.init = this.init.bind(this)
        this.add = this.add.bind(this)
        this.quote = this.quote.bind(this)
        this.buy = this.buy.bind(this)

    }

    init = () => {

        this.token = localStorage.getItem("token")

        let headers = {
            Accept: "application/json",
        };

        if (this.api_token) {
            headers.Authorization = `Bearer ${this.token}`;
        }

        this.client = axios.create({
            baseURL: this.api_url,
            timeout: 31000,
            headers: headers,
        });

        return this.client

    }

    getUserInfo = (userid) => {

        return new Promise((resolve, reject) => {

            axios.post("accountinfo", { userid: userid }).then( res => {

                if(res.data.error){

                    resolve({
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        data: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("Get Balance ")

        })

    }

    add = (userid, amount) => {

        return new Promise((resolve, reject) => {

            axios.post("add", { userid: userid, amount: amount }).then( res => {

                if(res.data.error){

                    resolve({
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        data: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("Add amount ")

        })

    }

    quote = (userid, stockSymbol) => {

        return new Promise((resolve, reject) => {

            axios.post("quote", { userid: userid, stockSymbol: stockSymbol }).then( res => {

                if(res.data.error){

                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        authenticated: true,
                        data: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("Quoted ")

        })

    }

    buy = (userid, stockSymbol, amount) => {

        return new Promise((resolve, reject) => {

            axios.post("buy", { userid: userid, stockSymbol: stockSymbol, amount: amount }).then( res => {

                if(res.data.error){

                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        authenticated: true,
                        data: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("We have run the add function ")

        })

    }

    commitBuy = (userid) => {

        return new Promise((resolve, reject) => {

            axios.post("commitbuy", { userid: userid}).then( res => {

                if(res.data.error){

                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        authenticated: true,
                        message: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("We have run the add function ")

        })

    }

    cancelBuy = (userid) => {

        return new Promise((resolve, reject) => {

            axios.post("cancelbuy", { userid: userid }).then( res => {

                if(res.data.error){

                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        authenticated: true,
                        message: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("We have run the add function ")

        })

    }

    sell = (userid, stockSymbol, amount) => {

        return new Promise((resolve, reject) => {

            axios.post("sell", { userid: userid, stockSymbol: stockSymbol, amount: amount }).then( res => {

                if(res.data.error){

                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    resolve({
                        authenticated: true,
                        message: res.data
                    })

                }

            }).catch(err => {
                console.log(err)
                reject()
            })

            console.log("We have run the add function ")

        })

    }

    commitSell = (userId) => {

        return new Promise((resolve, reject) => {

            axios.post("commitsell", { userId: userId }).then( res => {

                if(res.data.error){

                    this.err = res.data.error
                    resolve({
                        authenticated: false,
                        error: res.data.error
                    })

                }
                else{

                    this.authenticated = true;
                    this.token = res.data.token;
                    this.user = res.data.user;

                    localStorage.setItem("token", res.data.token);

                    console.log(res);
                    resolve({
                        authenticated: true,
                        message: res.data
                    })

                }

            }).catch(err => {
                console.log(err.data)
                reject()
            })

            console.log("We have run the log in function ")

        })

    }



}

export default new Transaction();
