import React, { Component } from 'react';
import transaction from '../../Helper/transaction';


//2 types of orders - triggered and market
class Order extends Component {

    constructor(){
        super()

        this.state = {
            ordertype : "",
            triggered : false,
            showConfirmationButtons : false,
        }


    }

    handleChange = (event) => {

        if(event.target.value === "triggered"){
            this.setState({
                triggered: true
            })
        }
        if(event.target.value === "market"){
            this.setState({
                triggered: false
            })
        }
    }

    buyOrSell = () => {
        this.setState({
            showConfirmationButtons : true
        })

    }

    //determine type of order and submit it
    makeOrder = (event) => {

        event.preventDefault()
        console.log(event.target.name)


        if(event.target.name === "Buy" || this.props.ordertype === "Sell"){

            this.setState({
                showConfirmationButtons: true
            })

            event.target.name === "Buy" ? transaction.buy() : transaction.sell()

        }

        if(event.target.name === "commit" || this.props.ordertype === "cancel"){

            this.setState({
                showConfirmationButtons: false
            })

            event.target.name === "Buy" ? transaction.buy() : transaction.sell()

        }




    }


    render() {

        let button;
        let confirmButtons;
        let cancelButtonName = "" + this.props.ordertype + ""
        let commitButtonName = this.props.ordertype

        button = <button name = {this.props.ordertype} type = "submit" onClick={this.makeOrder}>  {this.state.triggered ? "Set" : ""} {this.props.ordertype} </button>

        confirmButtons = <div>
                            <button name = {commitButtonName} type="submit" onClick={this.makeOrder}>Commit {this.state.triggered ? "Set" : ""} {this.props.ordertype}</button>
                            <button name = "Cancel" type="submit" onClick={this.makeOrder}>Cancel {this.state.triggered ? "Set" : ""} {this.props.ordertype}</button>
                        </div>


        return (
            <div className="Order">

                <h2>Place an order.</h2>

                <h3>{this.props.ordertype}</h3>

                <h3>{this.props.stockName}</h3>

                <h3>{this.props.stockCurrentPrice}</h3>

                <select name="order type" id="ordertype" onChange={this.handleChange}>
                    <option value="market">Market {this.props.ordertype}</option>
                    <option value="triggered">Set {this.props.ordertype} Trigger</option>
                </select>


                <form onSubmit={this.makeOrder}>

                    {this.props.ordertype} Amount: 

                    <input name="amount" type="number" min="1" onChange={this.handleChange}></input>

                    <br/>

                    {this.state.triggered && 
                        <div>
                            {this.props.ordertype} Trigger: 
                            <input name="triggerVal" type="number" min="1" onChange={this.handleChange}></input>
                        </div>
                    }

                    {this.state.showConfirmationButtons ? confirmButtons : button }

                </form>

            </div>
        );

    }
  }

  export default Order;
