import React, { Component } from 'react';
import Signup from './signup';
import { Link } from "react-router-dom";
import axios from 'axios';
import auth from './auth'



class Login extends Component {

    constructor(props){
        super(props);

        this.state = {
            err:"",
            authenticated:"",
        }

        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit = (event) => {
        event.preventDefault();

        auth.login(this.state.username, this.state.password).then((message) => {
            console.log(message);

            if(message.authenticated){
                this.setState({
                    authenticated: true
                });
            }
            else{
                this.setState({
                    err: message.error
                });
            }

        });

    }

    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    render() {
      return (
        <div className="login">

                <div class="vertical-container">

                    <form id='signin-form' onSubmit={this.handleSubmit}>
                        <h2>LOGIN</h2>
                        <input type="text" placeholder="Username" name='username' onChange={this.myChangeHandler}></input>
                        <input type="password" placeholder="Password" name='password' onChange={this.myChangeHandler}></input>
                        <button type="submit" >Sign In</button>
                    </form>

                    <div>
                        {this.state.authenticated ? '' : this.state.err}
                    </div>

                </div>


        </div>
      );
    }
  }

  export default Login;
