import React, { Component } from 'react';
import transaction from '../../Helper/transaction';



class Add extends Component {


    constructor(){
        super();

        this.state = {
            //will change this to props to the add component
            userid:"mark sigilai",
        }

      }


    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    handleSubmit = (event) => {

        event.preventDefault();

        transaction.add(this.state.userid, this.state.amount).then((res) => {
            console.log(res);
            this.setState({message: JSON.stringify(res.data.data.message)})
        });

    }


    render() {

      return (
        <div className="add">

            <form id='signin-form' onSubmit={this.handleSubmit}>
                <input name='amount' type="number" onChange={this.myChangeHandler}></input>
                <button type="submit" >Submit</button>

                <div>
                    Response: {this.state.message}
                </div>
            </form>

        </div>
      );
    }
  }

  export default Add;
