import React, { Component } from 'react';


class Order extends Component {

    constructor(){
        super()

        console.log("Hello")

    }

    handleChange = (event) => {
        console.log(event.target.value)
    }

    handleBuyOrder = () => {

    }

    handleSellOrder = () => {

    }

    render() {

        if(this.props.type === "Buy"){
            return (
                <div className="BuyOrder">

                    <h2>Place an order.</h2>

                    <h3>{this.props.type}</h3>

                    <h3>{this.props.stockName}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>

                    <select name="order type" id="ordertype" onChange={this.handleChange}>
                        <option value="marketbuy">Market Buy</option>
                        <option value="setbuytrigger">Set Buy Trigger</option>
                    </select>


                    <form>
                        Buy Amount: 
                        <input type="number" min="1"></input>
                        <br/>

                        {this.state.buytrigger && 
                            <div>
                                Buy Trigger: 
                                <input type="number" min="1"></input>
                                <br/>
                            </div>
                        }

                        <button type="submit">Submit Order</button>
                    </form>

                </div>
            );
        }


        return (
            <div className="SellOrder">

                <h3>{this.props.type}</h3>

                    <h2>Place an order.</h2>

                    <h3>{this.props.type}</h3>

                    <h3>{this.props.stockName}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>


                <form>
                    <select name="cars" id="cars">
                        <option value="marketsell">Market Sell</option>
                        <option value="setselltrigger">Set Sell Trigger</option>
                    </select>

                    <form>
                        Sell Amount: 
                        <input type="number" min="1"></input>
                        <br/>
                        Sell Amount: 
                        <input type="number" min="1"></input>
                        <br/>
                        <button type="submit">Submit Order</button>
                    </form>
                </form>

            </div>
        );


    }
  }

  export default Order;
