import React, { Component } from 'react';
import Buttons from "../Buttons/buttons"


//2 types of orders - triggered and market
class Order extends Component {

    constructor(){
        super()

        this.state = {
            ordertype : "",
            triggered : false,
            buttonClicked : false,
            confirmButtonClicked : false,
            cancelButtonClicked : false
        }

        console.log("Hello")

    }

    handleChange = (event) => {

        if(event.target.value === "triggered"){
            this.setState({
                triggered: true
            })
        }
        if(event.target.value === "market"){
            this.setState({
                triggered: false
            })
        }
    }

    handleBuyOrder = () => {

    }

    handleSellOrder = () => {

    }

    render() {

        let button;

        if(this.state.triggered){
            button = <button type = "submit">Set {this.props.ordertype}</button>
        }
        else{
            button = <button type = "submit">{this.props.ordertype}</button>
        }

        return (
            <div className="Order">

                <h2>Place an order.</h2>

                <h3>{this.props.ordertype}</h3>

                <h3>{this.props.stockName}</h3>

                <h3>{this.props.stockCurrentPrice}</h3>

                <select name="order type" id="ordertype" onChange={this.handleChange}>
                    <option value="market">Market {this.props.ordertype}</option>
                    <option value="triggered">Set {this.props.ordertype} Trigger</option>
                </select>


                <form>
                    {this.props.ordertype} Amount: 
                    <input type="number" min="1"></input>
                    <br/>

                    {this.state.triggered && 
                        <div>
                            {this.props.ordertype} Trigger: 
                            <input type="number" min="1"></input>
                            <br/>
                        </div>
                    }

                    {button}

                    {!this.state.triggered && 

                        <div>

                            <button type="submit">Commit {this.props.ordertype}</button>
                            <button type="submit">Cancel {this.props.ordertype}</button>
                        </div>

                    }

                </form>

            </div>
        );


    }
  }

  export default Order;
