import React, { Component } from 'react';


class Order extends Component {

    constructor(){
        super()

        console.log("Hello")

    }

    handleChange = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }

    handleBuyOrder = () => {

    }

    handleSellOrder = () => {

    }

    render() {

        if(this.props.type === "Buy"){
            return (
                <div className="BuyOrder">

                    <h2>Place an order.</h2>

                    <h3>{this.props.type}</h3>

                    <h3>{this.props.stockName}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>

                    <select name="order type" id="ordertype" onChange={this.handleChange}>
                        <option value="volvo">Market Buy</option>
                        <option value="saab">Set Buy Trigger</option>
                    </select>


                    <form>
                        Buy Amount: 
                        <input type="number" min="1"></input>
                        <br/>

                        Buy Trigger: 
                        <input type="number" min="1"></input>
                        <br/>

                        <button type="submit">Submit Order</button>
                    </form>

                </div>
            );
        }


        return (
            <div className="SellOrder">

                <h3>{this.props.type}</h3>

                    <h2>Place an order.</h2>

                    <h3>{this.props.type}</h3>

                    <h3>{this.props.stockName}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>

                    <h3>{this.props.stockCurrentPrice}</h3>


                <form>
                    <select name="cars" id="cars">
                        <option value="volvo">Market Sell</option>
                        <option value="saab">Set Sell Trigger</option>
                    </select>

                    <form>
                        Sell Amount: 
                        <input type="number" min="1"></input>
                        <br/>
                        Sell Amount: 
                        <input type="number" min="1"></input>
                        <br/>
                        <button type="submit">Submit Order</button>
                    </form>
                </form>

            </div>
        );


    }
  }

  export default Order;
