import React, { Component } from 'react';
import Stock from '../../Components/Stock/stock.js'
import './portfolio.css'
import transaction from '../../Helper/transaction';

class Portfolio extends Component {

  constructor(){
    super();

    this.state = {
      stocks : [{"Old stock":"No Stocks"}],
      balance : 3000
    }
  }

  async componentDidMount(){
    //obtain account info - name, balance, stocks owned = array of objects

    const response = await transaction.getStocks(localStorage.getItem("userid"))

    console.log(response)

    if(response.data){
        if(response.data.status === "passed"){
            this.setState({
                error: "",
                orderConfirmation: true,
                stocks: response.data.stocks
            })
            this.forceUpdate()
        }
        console.log(this.state.stocks)
    }
    else if(response.error){
        this.setState({
            error: response.error
        })
    }



  }

    render() {


      var stocklist = [];

      console.log("Thes tocks are ==", this.state.stocks)

      for(var i = 0; i < this.state.stocks.length; i++){
        console.log(this.state.stocks[i].name)
        stocklist.push(<Stock  name = {this.state.stocks[i].name} amount = {this.state.stocks[i].amount }/>)
      }


      return (
        <div className="Portfolio">

          <h2 className="Portfolio-header">Portfolio</h2>

          {stocklist}

        </div>
      );
    }
  }

  export default Portfolio;
