import React, { Component } from 'react';
import Signup from './signup';
import { Link } from "react-router-dom";
import axios from 'axios';



class Login extends Component {

    constructor(props){
        super(props);

        this.state = {
            username: "",
            password: "",
            err:""
        }

        this.handleSubmit = this.handleSubmit.bind(this);

    }

    componentDidMount() {

    }


    async handleSubmit(event){
        console.log("The form has been submitted");
        event.preventDefault();

        axios.post("http://localhost:8081/login", { username: this.state.username, password: this.state.password }).then(res => {
            console.log(res);
            console.log(res.token);
        }).catch(err => {
            console.log(err.Error);
        })

    }


    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val}, () => {});

    }


    render() {
      return (
        <div className="login">

                <div class="vertical-container">

                    <form id='signin-form' onSubmit={this.handleSubmit}>
                        <h2>LOGIN</h2>
                        <input type="text" placeholder="Username" name='username' onChange={this.myChangeHandler}></input>
                        <input type="password" placeholder="Password" name='password' onChange={this.myChangeHandler}></input>
                        <button type="submit" >Sign In</button>
                    </form>

                </div>

        </div>
      );
    }
  }

  export default Login;
