import React, { Component } from 'react';
import './App.css';
import Navbar from './components/Navbar/navbar';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Login from './components/Login/login';
import Homepage from './routes/Homepage/homepage';
import Landing from './routes/Landing/landing';
import Signup from './routes/Signup/signup';

import axios from 'axios';

class App extends Component {

  constructor(){
    super();

    this.state = {
      token:"",
      authenticated:false
    } 

    axios.get("express_backend").then( res => {
      console.log(res.data);
    })

  }

  setToken = (token) =>{
    console.log("we are setting the token in app..")

    this.setState({
      token: token, 
      authenticated: true
    })
  }

  render() {

    if(!this.state.token){

        return (
          <div>
            <Login setToken={ this.setToken } />

            <Routes>
              <Route exact path="/signup" element={ <Signup setToken={ this.setToken } /> }/>  
            </Routes>

          </div>
        )
    }

    return (
      <div class = "app">

          <div className="App"> App.js -- Hello I am the grandfather element </div>
          <Navbar authenticated={this.state.authenticated} />

          <Routes>
            <Route exact path="/homepage" element={ <Homepage /> }/>  
            <Route exact path="/" element={ <Landing /> }/>  
          </Routes>

      </div>
    );

  }


}

  export default App;
