import React, { Component } from 'react';
import transaction from '../../Helper/transaction';


//2 types of orders - triggered and market
class Order extends Component {

    constructor(){
        super()

        this.state = {
            ordertype : "",
            triggered : false,
            buttonClicked : false,
        }

        console.log("Hello")

    }

    handleChange = (event) => {

        if(event.target.value === "triggered"){
            this.setState({
                triggered: true
            })
        }
        if(event.target.value === "market"){
            this.setState({
                triggered: false
            })
        }
    }

    buyOrSell = () => {
        this.setState({
            buttonClicked : true
        })
    }

    //determine type of order and submit it
    makeOrder = (event) => {

        event.preventDefault()
        console.log(event.target)

    }


    render() {


        console.log("Triggered ? " + this.state.triggered)
        console.log("Order type ? " + this.props.ordertype)



        let button;
        let confirmButtons;

        button = <button name = "buy/sell" type = "submit" onClick={this.buyOrSell.bind(this)} >  {this.state.triggered ? "Set" : ""} {this.props.ordertype} </button>

        confirmButtons = <div>
                            <button name = "commit" type="submit" onClick={this.makeOrder.bind(this)} >Commit {this.state.triggered ? "Set" : ""} {this.props.ordertype}</button>
                            <button name = "cancel" type="submit" onClick={this.makeOrder.bind(this)} >Cancel {this.state.triggered ? "Set" : ""} {this.props.ordertype}</button>
                        </div>


        return (
            <div className="Order">

                <h2>Place an order.</h2>

                <h3>{this.props.ordertype}</h3>

                <h3>{this.props.stockName}</h3>

                <h3>{this.props.stockCurrentPrice}</h3>

                <select name="order type" id="ordertype" onChange={this.handleChange}>
                    <option value="market">Market {this.props.ordertype}</option>
                    <option value="triggered">Set {this.props.ordertype} Trigger</option>
                </select>


                <form onSubmit={this.makeOrder().bind(this)}>
                    {this.props.ordertype} Amount: 
                    <input type="number" min="1"></input>
                    <br/>

                    {this.state.triggered && 
                        <div>
                            {this.props.ordertype} Trigger: 
                            <input type="number" min="1"></input>
                        </div>
                    }

                    {!this.state.buttonClicked ? button : confirmButtons}

                </form>

            </div>
        );

    }
  }

  export default Order;
