import React, { Component } from 'react';


//2 types of orders - triggered and market
class Order extends Component {

    constructor(){
        super()

        this.state = {
            ordertype : "",
            triggered : false,
            buttonClicked : false,
        }

        console.log("Hello")

    }

    handleChange = (event) => {

        if(event.target.value === "triggered"){
            this.setState({
                triggered: true
            })
        }
        if(event.target.value === "market"){
            this.setState({
                triggered: false
            })
        }
    }

    //determine type of order and submit it
    handleOrder = (event) => {
        event.preventDefault()
        console.log(event)
    }

    render() {

        let button;
        let confirmButtons;
        let text = ""

        if(this.state.triggered){
            text  = "Set"
        }

        button = <button type = "submit" onClick={this.handleOrder} > {text} {this.props.ordertype}</button>

        confirmButtons = <div>
                            <button type="submit" onClick={() => {this.setState({buttonClicked : true})}} >Commit {text} {this.props.ordertype}</button>
                            <button type="submit">Cancel {text} {this.props.ordertype}</button>
                        </div>


        return (
            <div className="Order">

                <h2>Place an order.</h2>

                <h3>{this.props.ordertype}</h3>

                <h3>{this.props.stockName}</h3>

                <h3>{this.props.stockCurrentPrice}</h3>

                <select name="order type" id="ordertype" onChange={this.handleChange}>
                    <option value="market">Market {this.props.ordertype}</option>
                    <option value="triggered">Set {this.props.ordertype} Trigger</option>
                </select>


                <form>
                    {this.props.ordertype} Amount: 
                    <input type="number" min="1"></input>
                    <br/>

                    {this.state.triggered && 
                        <div>
                            {this.props.ordertype} Trigger: 
                            <input type="number" min="1"></input>
                            <br/>
                        </div>
                    }

                    {!this.state.buttonClicked ? button : confirmButtons}

                </form>

            </div>
        );


    }
  }

  export default Order;
