import React, { Component } from 'react';
import transaction from '../../Helper/transaction.js';



class Summary extends Component {


    displaySummary(){

      transaction.getSummary("userid").then((res) => {
        console.log(res)
      })

    }


    render() {
      return (
        <div className="homepage">

        <h3>Display Summary</h3>

        <div>Click the button below to generate a summary of all your transactions</div>

        <button onClick={this.displaySummary}>Display Summary </button>

        </div>
      );
    }

  }

  export default Summary;
