import React, { Component } from 'react';
import auth from './auth'


class Signup extends Component {

    constructor(props){
        super(props);

        this.state = {
            err:"",
            authenticated:"",
        }

        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit = (event) => {
        event.preventDefault();

        auth.signup(this.state.username, this.state.email, this.state.password).then((message) => {
            console.log(message);

            if(message.authenticated){
                this.setState({
                    authenticated: true,
                    err:""
                });
            }
            else{
                this.setState({
                    authenticated: false,
                    err: message.error
                });
            }

        });

    }

    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }

    render() {
        return (

            <form onSubmit={this.handleSubmit}>
                <h1>Sign up </h1>

                <label htmlFor="defaultFormRegisterNameEx" className="grey-text">
                    Your name
                </label>

                <input name="username" type="text" id="defaultFormRegisterNameEx" className="form-control" onChange={this.myChangeHandler}/>
                <br />

                <label htmlFor="defaultFormRegisterEmailEx" className="grey-text">
                    Your email
                </label>

                <input name="email" type="email" id="defaultFormRegisterEmailEx" className="form-control" onChange={this.myChangeHandler}/>
                <br />

                <label htmlFor="defaultFormRegisterConfirmEx" className="grey-text">
                    Confirm your email
                </label>

                <input type="email" id="defaultFormRegisterConfirmEx" className="form-control" onChange={this.myChangeHandler}/>
                <br />

                <label htmlFor="defaultFormRegisterPasswordEx" className="grey-text">
                    Your password
                </label>

                <input type="password" id="defaultFormRegisterPasswordEx" className="form-control" onChange={this.myChangeHandler}/>

                <div className="text-center mt-4">
                    <button color="unique" type="submit">Register</button>
                </div>
            </form>

        );
    }
  }

  export default Signup;
