import React, { Component } from 'react';
import transaction from '../../Helper/transaction';
import Order from "../Order/order"

class Quote extends Component {

    constructor(){
        super()

        this.state = {
            stock: "",
            error: ""
        }
    }



    myChangeHandler = (event) => {

        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]: val});

    }


    handleSubmit = (event) => {

        event.preventDefault();

        transaction.quote(this.state.userid, this.state.stock).then((res) => {
            console.log(res.data);

            if(res.data.name === "Error" || res.error){
                this.setState({error: JSON.stringify(res.data.message)})
            }
            else{
                this.setState({message: JSON.stringify(res.data)})
            }
        });

    }



    render() {

        let content;

        if(this.state.error){
            content = "There was an error, try again"

        }
        else if(this.state.message){

            //make an order straigt from a quote
            content =  <Order type="Buy" />

        }

      return (
        <div className="Quote">

            <form onSubmit={this.handleSubmit}>
                <h1>Search for a stock</h1>
                <input name="stock" type="text" onChange={this.myChangeHandler}/>
                <button type="submit">Submit</button>
            </form>

            <Order type="Buy" />

            {content}

        </div>
      );
    }
  }

  export default Quote;
